<script lang="ts">
import Vue from 'vue'

import { validate } from 'email-validator'

export default Vue.extend({
	layout: 'fullwidth',
	head() {
		return {
			title:
				'Sitios Web rápidos y elegantes con bajo costo de mantenimiento | Lautaro Pereyra',
			meta: [
				{
					hid: 'description',
					name: 'description',
